import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
  const [data, setData] = useState({ password: "", email: "" });
  const navigate = useNavigate();

  const HandleEvent = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3000/api/UserLogin", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: data.email,
          password: data.password,
        }),
      });

      const json = await response.json();

      if (!response.ok) {
        alert(json.error || "Something went wrong.");
      } else {
        console.log("Success:", json);
        alert("Logged in successfully!");
      }
      if (response.ok) {
        localStorage.setItem("UserEmail", data.email);
        localStorage.setItem("authToken", json.authToken);
        navigate("/");
      }
    } catch (error) {
      console.error("Error:", error.message);
      alert("Failed to connect to the server.");
    }
  };

  return (
    <section className="bg-gradient-to-br from-yellow-100 via-red-100 to-orange-100 min-h-screen flex items-center justify-center p-4">
      <div className="flex flex-col md:flex-row items-center bg-white shadow-lg rounded-xl overflow-hidden w-full max-w-4xl">
        {/* Left Section with Image */}
        <div className="hidden md:block md:w-1/2">
          <img
            src="data:image/jpeg;base64,"
            alt="Food Delivery"
            className="h-full w-full object-cover"
          />
        </div>

        {/* Right Section with Form */}
        <div className="w-full md:w-1/2 p-8">
          <div className="text-center mb-6">
            <h1 className="text-2xl font-bold text-gray-800">
              Welcome to Food Delivery!
            </h1>
            <p className="text-gray-600">Log in to access your account</p>
          </div>
          <form className="space-y-6" onSubmit={HandleEvent}>
            <div>
              <label
                htmlFor="email"
                className="block text-sm font-medium text-gray-700"
              >
                Email Address
              </label>
              <input
                type="email"
                name="email"
                value={data.email}
                onChange={(e) => setData({ ...data, email: e.target.value })}
                className="w-full mt-1 text-black p-3 border rounded-lg focus:outline-none focus:ring focus:ring-yellow-400"
                placeholder="name@example.com"
                required
              />
            </div>
            <div>
              <label
                htmlFor="password"
                className="block text-sm font-medium text-gray-700"
              >
                Password
              </label>
              <input
                type="password"
                name="password"
                value={data.password}
                onChange={(e) => setData({ ...data, password: e.target.value })}
                className="w-full text-black mt-1 p-3 border rounded-lg focus:outline-none focus:ring focus:ring-yellow-400"
                placeholder="••••••••"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full py-3 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 transition"
            >
              Login
            </button>
          </form>
          <p className="text-center text-sm text-gray-600 mt-4">
            Don’t have an account yet?{" "}
            <Link to="/SignIn" className="text-yellow-600 hover:underline">
              Sign up
            </Link>
          </p>
        </div>
      </div>
    </section>
  );
};

export default Login;
